// Create our app vars here
var appVars = {
	width: 900,
	height:900,
	viewAngle:45,
	near: 0.1,
	far:1000,
	container: $('#three'),
	renderer: new THREE.WebGLRenderer(),
	camera: null,
	scene: new THREE.Scene(),
	objects: {}
};

appVars.aspect = appVars.width / appVars.height;

appVars.camera = new THREE.PerspectiveCamera(	
	appVars.viewAngle,
    appVars.aspect,
    appVars.near,
   	appVars.far
);


// used for creating shapes

function addSphere(rad, seg, rings, mat, dynamic){
	
	var sphere = new THREE.Mesh( 
		new THREE.SphereGeometry(rad, seg, rings),
		mat
	);

	if(typeof(dynamic) !== undefined && dynamic){
		sphere.overdraw = true;
	}
	
	appVars.scene.add(sphere);

	return sphere;
}

