// messing arround with key controls

function addControls(callback) {
    // State of the different controls
    direction = {
        left: false,
        up: false,
        right: false,
        down: false
    };

    // When the user presses a key 
    $(document).keydown(function (e) {

        e.preventDefault();

        switch (e.keyCode) {
            case 37:
                direction.left = true;
                break;
            case 38:
                direction.up = true;
                break;
            case 39:
                direction.right = true;
                break;
            case 40:
                direction.down = true;
                break;
            default:
                prevent = false;
        }

        callback(direction);
    });

    $(document).keyup(function (e) {
        e.preventDefault();

        switch (e.keyCode) {
            case 37:
                direction.left = false;
                break;
            case 38:
                direction.up = false;
                break;
            case 39:
                direction.right = false;
                break;
            case 40:
                direction.down = false;
                break;
            default:
                prevent = false;
        }

        callback(direction);
    });
}
// This is where we store all of our leap motion functions


// used to get the position using the interaction box
function getLeapPosition(frame, leapPos){

	/* 
	 * Interaction box will scale to wherever the uses interactions are and will contain the interaction
	 * inside a box so we can convert the positions easier
	 */

	var iBox = frame.interactionBox,
	 	top = iBox.center[1] + iBox.size[1]/2,
  		left = iBox.center[0] - iBox.size[0]/2,
  		position = {};

  	position.x = leapPos[0] - left,
  	position.y = leapPos[1] - top;

	position.x /= iBox.size[0];
  	position.y /= iBox.size[1];

  	position.x *= appVars.width;
  	position.y *= appVars.height;


  	return position;
}
// All leap motion code in here for now

$(window).ready(function(){
	
	/*var canvas = document.getElementById('leap'),
		c = canvas.getContext('2d'),
		controller = new Leap.Controller();
	*/

	var controller = new Leap.Controller();


	controller.on('connect', function(){

		
	});

	/*
	 * This is the core function for the leap controller here we detect whats going on and act on it
	 */
	controller.on( 'animationFrame' , function( frame ) {

		// for every hand picked up
		for( var i = 1; i <= frame.hands.length; i++ ){

			var hand = frame.hands[i -1];

			if(hand.palmPosition){
				var palmPos = getLeapPosition(frame, hand.palmPosition);
				setObjectPosition('globes', i, palmPos);
			}
			
			// for every finger on that hand
			for( var j = 1; j <= hand.fingers.length; j++ ){

			  	var finger = hand.fingers[j -1];

			  	if(finger.fingerPosition){
			  		var fingerPos = getLeapPosition(frame, finger.fingerPosition);
			  		setObjectPosition('globes', i+j, fingerPos);
		  		}

			}
		}
    
  	});


	// start the leap motion!!!! :D
	controller.connect();

})

